mapper.select:
SELECT classroom_id
FROM ted_plan_classroom
WHERE plan_id = #{planId}

SELECT COUNT(tep.id) FROM ted_exam_plan tep LEFT JOIN ted_plan_classroom tpc ON tep.id = tpc.plan_id WHERE tep.`status` NOT IN (4, 6) AND tep.is_deleted = 0 AND tpc.classroom_id IN
#{id}
AND #{startTime} < DATE_SUB(tep.end_time, INTERVAL 30 MINUTE) AND #{endTime} > DATE_ADD(tep.start_time, INTERVAL 30 MINUTE)

SELECT DISTINCT tlc.location_id FROM ted_exam_plan tep LEFT JOIN ted_plan_classroom tpc ON tep.id = tpc.plan_id LEFT JOIN ted_location_classroom tlc ON tpc.classroom_id = tlc.classroom_id AND tlc.is_deleted = 0 WHERE tep.id IN
#{id}
AND tep.is_deleted = 0

mapper.update:
UPDATE ted_exam_plan
SET max_candidates = CASE id

    WHEN #{plan.id} THEN #{plan.maxCandidates}

END
WHERE id IN

    #{plan.id}
