SELECT tep.id AS planId, tep.exam_plan_name AS planName, tp.is_operation AS isOperation, CASE WHEN tc.id = 21 THEN 1 ELSE 0 END AS isRoad FROM ted_exam_plan tep LEFT JOIN ted_project tp ON tep.exam_project_id = tp.id AND tp.is_deleted = 0 LEFT JOIN ted_category tc ON tp.category_id = tc.id AND tc.is_deleted = 0 WHERE tep.id IN
#{id}
AND tep.is_deleted = 0

SELECT er.id AS record_id, GROUP_CONCAT(twa.welding_project_code) AS welding_project_codes FROM ted_exam_records er JOIN ted_enroll te ON er.plan_id = te.exam_plan_id AND er.candidate_id = te.user_id AND te.is_deleted = 0 JOIN sys_user su ON te.user_id = su.id JOIN ted_worker_apply twa ON te.class_id = twa.class_id AND su.username = twa.id_card_number AND twa.is_deleted = 0 WHERE er.id IN
#{id}
GROUP BY er.id
