mapper.select:
select distinct o.id as id, o.code as code, o.name as name, o.social_code as socialCode, o.location as location, o.legal_person as legalPerson, o.scale as scale, o.business_license as businessLicense, o.create_time as createTime, o.update_user as updateUser, o.update_time as updateTime, o.is_deleted as asisDeleted from ted_org as o
join ted_org_candidate as c on o.id = c.org_id
${ew.customSqlSegment}

select id, org_id, status, remark,payment_status
from ted_org_candidate
where is_deleted = 0
and org_id = #{orgId}
and candidate_id = #{userId}

    and project_id = #{projectId}

limit 1

mapper.update:
update ted_org_candidate
set status = 2
where is_deleted = 0
  and org_id = #{orgId}
  and candidate_id = #{userId}

update ted_org_candidate
set status = -1
where is_deleted = 0
  and org_id = #{orgId}
  and candidate_id = #{userId}

UPDATE ted_org_candidate
SET is_deleted = 1,
    status     = 0
WHERE org_id = #{orgId}
  AND candidate_id = #{userId}
  AND is_deleted = 0
