mapper.select:
SELECT tp.project_name as `label`, tp.id as `value` FROM ted_project AS tp WHERE tp.is_deleted = 0
AND tp.dept_id = #{deptId}
and tp.project_status = 2

SELECT ted.ted_project.id AS `value`, ted_project.project_name AS label
FROM ted_project
WHERE project_status = 2;

SELECT category_id AS parent_id, id AS `value`, project_name AS `label` FROM ted_project WHERE category_id IN
#{id}
AND is_deleted = 0
